import React from "react";
import Tool from "conpo"

function WordCounter() {
  return (
    <div className="word_counter">
      <h3>
        <i className="fa-solid fa-house" />
        Word Counter
      </h3>
      <Tool />
      <textarea
        type="text"
        placeholder="Enter/Paste Your Text Here"
        defaultValue={""}
      />
      <div className="word_counter_footer">
        <button>Click to Uppercase</button>
        <button>Click to Lowercse</button>
      </div>
    </div>
  );
}

export default WordCounter;
